import React from 'react';
import './Portfolio.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { NextArrow, PrevArrow } from './Arrowcomponent.jsx';

// Import images
import AvsH from "../../img/AvsH.png";
import Directors1 from "../../img/Directors1.jpg";
import GAMING from "../../img/GAMING.jpg";
import Scary from "../../img/Scary.jpg";
import RichPoor from "../../img/RichPoor.jpg";
import Trumpp from "../../img/Trumpp.jpg";

import Heroslider from "react-slick";

const Portfolio = () => {
    const settingsLg = {
        arrows: true,
        autoplay: true,
        centerMode: true,
        centerPadding: "20px", // Adjust as needed
        dots: true,
        infinite: true,
        speed: 800,
        slidesToShow: 3, // Show 3 slides at once
        slidesToScroll: 1,
        prevArrow: <PrevArrow />,
        nextArrow: <NextArrow />,
        rows: 1
    };

    const settings = {
        arrows: true,
        autoplay: true,
        centerMode: true,
        centerPadding: "10px", // Adjust as needed
        speed: 800,
        slidesToShow: 3, // Show 3 slides at once
        slidesToScroll: 1,
        infinite: true,
        prevArrow: <PrevArrow />,
        nextArrow: <NextArrow />,
        rows: 1
    };

    const images = [
        AvsH, Directors1, GAMING, Scary, RichPoor, Trumpp
    ];

    return (
        <>
            <div className="slider-mobile">
                <Heroslider {...settings}>
                    {images.map((image, index) => (
                        <div key={index} className="slick-slide">
                            <img src={image} alt={`Slide ${index}`} />
                        </div>
                    ))}
                </Heroslider>
            </div>

            <div className="slider-lg">
                <Heroslider {...settingsLg}>
                    {images.map((image, index) => (
                        <div key={index} className="slick-slide">
                            <img src={image} alt={`Slide ${index}`} />
                        </div>
                    ))}
                </Heroslider>
            </div>
        </>
    );
};
