import React, { useState, useRef } from 'react';
import './Contact.css';
import emailjs from '@emailjs/browser';

export const Contact = () => {
  const form = useRef(null);
  const [done, setDone] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();
    console.log('Form submitted');

    emailjs
      .sendForm('service_45qrc09', 'template_yz3bb76', form.current, '3b3q') // Replace with your user ID
      .then(
        () => {
          console.log('SUCCESS!');
          setDone(true);
        },
        (error) => {
          console.log('FAILED...', error.text);
        }
      );
  };

  return (
    <div className="contact-form" id="Contact">
      <div className="w-left">
        <div className="awesome">
          <span className="block-text primary-text">Get in Touch</span>
          <span className="block-text secondary-text">Contact Me</span>
          <div className="blur s-blurl" style={{ background: "#ABF1FF94" }}></div>
        </div>
      </div>

      <div className="c-right">
        <form ref={form} onSubmit={sendEmail}>
          <input type="text" name="user_name" className="user" placeholder="Name" required />
          <input type="email" name="user_email" className="user" placeholder="Email" required />
          <textarea name="message" className="user" placeholder="Message" required />
          <input type="submit" value="Send" className="button" />
          <span>{done && "   Thanks for Contacting me!"}</span>
          <div className="blur c-blurl" style={{ background: "var(--purple)" }}></div>
        </form>
      </div>
    </div>
  );
};
