import React from "react";
import Heroslider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { NextArrow, PrevArrow } from "./ArrowComponent.jsx";

import AvsH from "../../img/AvsH.png";
import Directors1 from "../../img/Directors1.jpg";
import GAMING from "../../img/GAMING.jpg";
import Scary from "../../img/Scary.jpg";
import RichPoor from "../../img/RichPoor.jpg";
import Trumpp from "../../img/Trumpp.jpg";

export const HeroCarousal = () => {
  const settingsLg = {
    arrows: true,
    autoplay: true,
    centerMode: true,
    centerPadding: "100px",  // Adjust this as needed
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    prevArrow: <PrevArrow />,
    nextArrow: <NextArrow />,
  };

  const settings = {
    arrows: true,
    autoplay: true,
    centerMode: true,
    centerPadding: "0px",  // Adjust this as needed
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite: true,
    prevArrow: <PrevArrow />,
    nextArrow: <NextArrow />,
  };

  const images = [
    Scary,
    AvsH,
    Directors1,
    RichPoor,
    GAMING,
    Trumpp,
  ];

  return (
    <>
      <div className="lg:hidden">
        <Heroslider {...settings}>
          {images.map((image, index) => (
            <div key={index} className="w-full h-64 px-4 py-3"> {/* Adjust height and padding */}
              <img src={image} alt={`slide-${index}`} className="w-full h-full object-cover rounded-lg" />
            </div>
          ))}
        </Heroslider>
      </div>

      <div className="hidden lg:block">
        <Heroslider {...settingsLg}>
          {images.map((image, index) => (
            <div key={index} className="w-full h-96 px-4 py-3"> {/* Adjust height and padding */}
              <img src={image} alt={`slide-${index}`} className="w-full h-full object-cover rounded-lg" />
            </div>
          ))}
        </Heroslider>
      </div>
    </>
  );
};
