import React from "react";
import Heroslider from "react-slick";

// Import css files
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import {NextArrow, PrevArrow} from"./ArrowComponent.jsx";


import AvsH from "../../img/AvsH.png";
import Directors1 from "../../img/Directors1.jpg";
import GAMING from "../../img/GAMING.jpg";
import Scary from "../../img/Scary.jpg";
import RichPoor from "../../img/RichPoor.jpg";
import Trumpp from "../../img/Trumpp.jpg"

 export const HeroCarousal = () => {
    const settingsLg = {
        arrows:true,
        autoplay:true,
        centerMode:true,
        centerPadding:"120px",
        dots: true,
        infinite: true,
        speed: 800,
        slidesToShow: 1,
        slidesToScroll: 1,
        prevArrow:<PrevArrow/>,
        nextArrow:<NextArrow/>
    }
   const settings= {
    arrows:true, 
    autoplay:true,
    centerMode:true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    infinite:true,
    prevArrow:<PrevArrow/>,
    nextArrow:<NextArrow/>
   }

   const images =[
    {Scary},
    {AvsH},
    {Directors1},
    {RichPoor} ,
    {GAMING},
    {Trumpp}
  ]
   return (
    <>
    <div className="lg:hidden">
    <Heroslider {...settings}>
        {
            images.map((image)=> (
                <div className="w-full h-44 md:64 py-3">
                    <img src={image} alt="testing" className="w-full h-full"/>
                </div>
            ))
}
    </Heroslider>
    </div>

    <div className="hidden lg:block">
    <Heroslider {...settingsLg}>
        {
            images.map((image)=> (
                <div className="w-full h-96 px-2 py-3 ">
                    <img src={image} alt="testing" className="w-full h-full"/>
                </div>
            ))
}
    </Heroslider>
    </div>
    </>
   ) 
};
