import React from 'react'
import './Portfolio.css'
import {Swiper,SwiperSlide} from 'swiper/react';
import 'swiper/css';

import AvsH from "../../img/AvsH.png";
import Directors1 from "../../img/Directors1.jpg";
import GAMING from "../../img/GAMING.jpg";
import Scary from "../../img/Scary.jpg";
import RichPoor from "../../img/RichPoor.jpg";
import Trumpp from "../../img/Trumpp.jpg"


export const Portfolio=()=>{
    return (
        <div className="portfolio">
               
               <span>Recent Projects</span>
               <span>Thumbnails</span>


                  <Swiper
                spaceBetween={0}
                slidesPerView={2}  // Shows 2 images per view
                slidesPerGroup={2} // Slides 2 images at a time
                grabCursor={true}
                className='portfolio-slider'
                direction='horizontal' // Ensure horizontal sliding
            >
                <SwiperSlide>
                    <img src={Scary} alt="Scary Project" />
                </SwiperSlide>
                <SwiperSlide>
                    <img src={AvsH} alt="AvsH Project" />
                </SwiperSlide>
                <SwiperSlide>
                    <img src={Trumpp} alt="Trumpp Project" />
                </SwiperSlide>
                <SwiperSlide>
                    <img src={RichPoor} alt="RichPoor Project" />
                </SwiperSlide>
                <SwiperSlide>
                    <img src={GAMING} alt="Gaming Project" />
                </SwiperSlide>
            </Swiper>

            </div>
        )
}
