import React,{useState} from 'react';
import './Contact.css';
import emailjs from '@emailjs/browser';
import {useRef} from 'react';

export const Contact = () => {
const form =useRef();
const [done,setDone]=useState(false)
    const sendEmail = (e) => {
        e.preventDefault();
   

      emailjs
      .sendForm('service_43oi7bo', 'template_6j5z9bx', form.current, 'YsO6gWE9vD2eK6pmyh')
      .then(
        () => {
          console.log('SUCCESS!');
          setDone(true);
        },
        (error) => {
          console.log('FAILED...', error.text);
        }
      );
    


  return (
    <div className="contact-form" id="Contact">
      <div className="w-left">
        <div className="awesome">
          <span className="block-text primary-text">Get in Touch</span>
          <span className="block-text secondary-text">Contact Me</span>
          <div
            className="blur s-blurl"
            style={{ background: "#ABF1FF94" }}
          ></div>
        </div>
      </div>

      <div className="c-right">
        <form ref={form} onSubmit={sendEmail}>
          <input
            type="text"
            name="user_name"
            className="user"
            placeholder="Name"
          />
          <input
            type="email"
            name="user_email"
            className="user"
            placeholder="Email"
          />
          <textarea name="message" className="user" placeholder="Message" />
          <input type="submit" value="Send" className="button" />
          <span>{done&& "   Thanks for Contacting me!"}</span>
          <div
            className="blur c-blurl"
            style={{ background: "var(--purple)" }}
          ></div>
        </form>
      </div>
    </div>
  )
};
};
