import React from "react";
import Heroslider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { NextArrow, PrevArrow } from "./Arrowcomponent.js";
import "./HeroCarousal.css"; // Import the CSS file

const HeroCarousal = () => {
    const settingsLg = {
        arrows: true,
        autoplay: true,
        centerMode: true,
        centerPadding: "120px",
        dots: true,
        infinite: true,
        speed: 800,
        slidesToShow: 1,
        slidesToScroll: 1,
        prevArrow: <PrevArrow />,
        nextArrow: <NextArrow />
    };

    const settings = {
        arrows: true,
        autoplay: true,
        centerMode: true,
        speed: 800,
        slidesToShow: 1,
        slidesToScroll: 1,
        infinite: true,
        prevArrow: <PrevArrow />,
        nextArrow: <NextArrow />
    };

    const images = [
        "https://th.bing.com/th/id/OIP.D5XW_gJ-Uu6Fi8fCniYWZwHaEK?w=304&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7",
        "https://assets-in.bmscdn.com/promotions/cms/creatives/1697784852917_edsheerandesktoprevised.jpg",
        "https://assets-in.bmscdn.com/promotions/cms/creatives/1690186957743_sunburnreviseddesktop.jpg",
        "https://assets-in.bmscdn.com/promotions/cms/creatives/1687328911358_webbannernpa.jpg",
        "https://static1.cbrimages.com/wordpress/wp-content/uploads/2020/12/Sung-Jin-Woo-Feature-Image.jpg"
    ];

    return (
        <>
            <div className="slider-mobile lg:hidden">
                <Heroslider {...settings}>
                    {images.map((image, index) => (
                        <div key={index}>
                            <img src={image} alt={`Slide ${index}`} />
                        </div>
                    ))}
                </Heroslider>
            </div>

            <div className="slider-lg hidden lg:block">
                <Heroslider {...settingsLg}>
                    {images.map((image, index) => (
                        <div key={index}>
                            <img src={image} alt={`Slide ${index}`} />
                        </div>
                    ))}
                </Heroslider>
            </div>
        </>
    );
};

export default HeroCarousal;
